mapper.update:
UPDATE userInfo
name = #{name}, phone_number = #{phone_number}, email = #{email}, nickname = #{nickname}, memo = #{memo}
WHERE id = #{id}

mapper.select:

SELECT * 
FROM userInfo
WHERE name LIKE CONCAT('%', #{name}, '%');


SELECT * 
FROM userInfo
WHERE phone_number LIKE CONCAT('%', #{phone_number}, '%');
